let points=[],tickSpeed=10,base=180,numPoints=5,maxTicks=300,ticks=2;function Point(i=random(width),t=random(height),o=random(PI)){this.x=i,this.y=t,this.a=o,this.dx=cos(o),this.dy=sin(o),this.color=color(87,137,47,.8)}function setup(){createCanvas(),colorMode(RGB),windowResized(),strokeWeight(1)}function init(){points=[],base=random(360),ticks=0;for(var i=0;i<10;i++)points.push(new Point);for(var i=0;i<points.length;i++){let t=i;for(;t==i;)t=floor(random(points.length));points[i].neighbor=points[t]}}function draw(){if(!(ticks>maxTicks))for(var i=0;i<tickSpeed;i++){for(var t=0;t<points.length;t++)points[t].update();ticks++}}function mouseClicked(){windowResized()}function windowResized(){resizeCanvas(windowWidth,windowHeight),pixelDensity(1),clear(),background(37,38,39),init()}Point.prototype.update=function(){this.x+=this.dx,this.y+=this.dy,(this.x<0||this.x>=width)&&(this.dx*=-1),(this.y<0||this.y>=height)&&(this.dy*=-1),stroke(this.color),line(this.x,this.y,this.neighbor.x,this.neighbor.y)};